(function () {
  "use strict";

  /* ===============================
     PROJECT DATABASE
  =============================== */
  const project_db = [
    {
      name: "Akshat Network Hub",
      prefix: "https://akshat-881236.github.io/",
      termsUrl: "https://akshat-881236.github.io/TrackerJS/terms.htm",
      privacyUrl: "https://akshat-881236.github.io/TrackerJS/privacy.htm",
      manualUrl: "https://akshat-881236.github.io/TrackerJS/usage-manual.htm"
    }
  ];

  const currentURL = window.location.href;

  const project = project_db.find(p =>
    currentURL.startsWith(p.prefix)
  );

  if (!project) return;

  /* ===============================
     CSS INJECTION
  =============================== */
  const style = document.createElement("style");
  style.innerHTML = `
  #anh-policy-band{
    position:fixed;
    top:0;
    left:0;
    width:100%;
    z-index:999999;
    display:flex;
    justify-content:center;
    font-family:system-ui,-apple-system,BlinkMacSystemFont,sans-serif;
  }
  .anh-card{
    margin:10px;
    padding:16px 20px;
    max-width:1100px;
    width:95%;
    background:#0f172a;
    color:#e5e7eb;
    border-radius:14px;
    box-shadow:0 10px 40px rgba(0,0,0,.35);
    display:flex;
    align-items:center;
    justify-content:space-between;
    gap:15px;
    animation:slideDown .6s ease;
  }
  @keyframes slideDown{
    from{transform:translateY(-120%);opacity:0}
    to{transform:translateY(0);opacity:1}
  }
  .anh-text{
    font-size:14px;
    line-height:1.5;
  }
  .anh-text b{
    color:#38bdf8;
  }
  .anh-actions{
    display:flex;
    gap:10px;
    flex-wrap:wrap;
  }
  .anh-btn{
    text-decoration:none;
    padding:8px 14px;
    border-radius:10px;
    font-size:13px;
    font-weight:600;
    transition:.25s;
    white-space:nowrap;
  }
  .anh-btn.primary{
    background:#38bdf8;
    color:#020617;
  }
  .anh-btn.primary:hover{
    background:#0ea5e9;
  }
  .anh-btn.secondary{
    background:#020617;
    color:#e5e7eb;
    border:1px solid #334155;
  }
  .anh-btn.secondary:hover{
    background:#020617;
    border-color:#38bdf8;
  }
  `;
  document.head.appendChild(style);

  /* ===============================
     BANNER CREATION
  =============================== */
  function showPolicyBand() {
    if (document.getElementById("anh-policy-band")) return;

    const band = document.createElement("div");
    band.id = "anh-policy-band";

    band.innerHTML = `
      <div class="anh-card">
        <div class="anh-text">
          <b>${project.name}</b> follows a unified  
          <a href="${project.termsUrl}" class="anh-btn secondary" target="_blank">Terms of Use</a>
          &  
          <a href="${project.privacyUrl}" class="anh-btn secondary" target="_blank">Privacy Policy</a>
          across all pages and sub-pages.
        </div>
        <div class="anh-actions">
          <a href="${project.manualUrl}" target="_blank" class="anh-btn primary">
            ðŸ“˜ Read Usage Manual
          </a>
        </div>
      </div>
    `;

    document.body.appendChild(band);
  }

  /* ===============================
     DELAY (2 MINUTES)
  =============================== */
  setTimeout(showPolicyBand, 1200);

})();