(function(){
"use strict";

/* =============================
   CONFIG
============================= */
var ALLOWED_PREFIX = "https://akshat-881236.github.io/";
var GOOD_DURATION = 3000;
var BAD_DURATION  = 7000;

/* =============================
   MESSAGE DATABASE (12+)
============================= */
var GOOD_MESSAGES = [
	"Welcome to Akshat Project Network",
	"Official Akshat Network page loaded",
	"Verified project environment",
	"Secure network detected",
	"Trusted repository accessed",
	"Akshat Network integrity confirmed"
];

var BAD_MESSAGES = [
	"You are outside Akshat Project Network",
	"Unverified website detected",
	"This page is not under Akshat control",
	"External domain loaded",
	"Proceed with caution",
	"Security notice: outside network"
];

/* =============================
   UTIL
============================= */
function pick(arr){
	return arr[Math.floor(Math.random() * arr.length)];
}

/* =============================
   BANNER UI
============================= */
function showBanner(text, bg, duration){
	var banner = document.createElement("div");

	banner.innerHTML = `
	<style>
		#ak-banner{
			position:fixed;
			top:0;
			left:0;
			right:0;
			z-index:999999;
			padding:12px;
			text-align:center;
			font-family:system-ui,Arial;
			font-size:14px;
			color:#fff;
			background:${bg};
			box-shadow:0 2px 6px rgba(0,0,0,.2);
			animation:slideDown .3s ease;
		}
		@keyframes slideDown{
			from{transform:translateY(-100%)}
			to{transform:translateY(0)}
		}
	</style>
	<div id="ak-banner">${text}</div>
	`;

	document.body.appendChild(banner);

	setTimeout(function(){
		banner.remove();
	}, duration);
}

/* =============================
   MAIN LOGIC
============================= */
document.addEventListener("DOMContentLoaded", function(){

	var url = window.location.href;
	var isAllowed = url.indexOf(ALLOWED_PREFIX) === 0;

	if(isAllowed){
		showBanner(
			pick(GOOD_MESSAGES),
			"#1e8e3e",     // green
			GOOD_DURATION
		);
	}else{
		showBanner(
			pick(BAD_MESSAGES),
			"#d93025",     // red
			BAD_DURATION
		);
	}

});

})();